example.programlisting:
module foo;

  // coverage off
  ...
  // coverage on

endmodule;

module foobar;

  reg b, c, d, e;

  // coverage off

  wire a;

  // coverage off
  assign a = b & c;
  // coverage on

  assign e = ~d;
  // coverage on

endmodule

para.programlisting:
module main;
  foo a();
  bar b();
endmodule

module foo;
  integer a;
  initial begin
    #10;
    a = $random;
  end
endmodule

module bar
  integer a;
  initial begin
    #5;
    a = $random;
  end
endmodule
